const songsReducer = () => {
	return [
		{title: 'Xanny', duration: '4:26'},
		{title: 'History', duration: '3:28'}
	];
};

const selectedSongReducer = (selectedSong=null) => {
	if(action.type === 'SONG_SELECTED'){
		return action.payload;
	}

	return selectedSong;
};
